CodeMirror.registerHelper("lint","css",function(e){var t=[],n=CSSLint.verify(e),r=n.messages,i=null;for(var s=0;s<r.length;s++){i=r[s];var o=i.line-1,u=i.line-1,a=i.col-1,f=i.col;t.push({from:CodeMirror.Pos(o,a),to:CodeMirror.Pos(u,f),message:i.message,severity:i.type})}return t});