// Depends on jsonlint.js from https://github.com/zaach/jsonlint
// declare global: jsonlint
CodeMirror.registerHelper("lint","json",function(e){var t=[];jsonlint.parseError=function(e,n){var r=n.loc;t.push({from:CodeMirror.Pos(r.first_line-1,r.first_column),to:CodeMirror.Pos(r.last_line-1,r.last_column),message:e})};try{jsonlint.parse(e)}catch(n){}return t}),CodeMirror.jsonValidator=CodeMirror.lint.json;